BEGIN TRANSACTION;
CREATE TABLE estimations (id integer primary key autoincrement, task_id integer, complexity_symbol text, unit integer DEFAULT 1, unit_value integer );
CREATE TABLE sqlite_sequence(name,seq);
CREATE TABLE stories ( id integer primary key autoincrement, task_id integer, name text, description text, consensus integer DEFAULT -1,iteration integer DEFAULT 0);
CREATE TABLE story_user_estimations ( id integer primary key autoincrement, story_id integer, user_id integer, estimation_id integer, story_iteration integer);
CREATE TABLE task_team ( id integer primary key autoincrement, user_id integer, task_id integer );
CREATE TABLE tasks ( id integer primary key autoincrement, name text, description text, created_at datetime DEFAULT (datetime('now', 'localtime')), published_at datetime );
CREATE TABLE user_estimations ( id integer primary key autoincrement, user_id integer, story_id integer, estimation_id integer);
CREATE TABLE users ( id integer primary key autoincrement, name text);
CREATE TABLE users_worktable ( id integer primary key autoincrement, user_id integer, story_id integer, estimation_id integer);
COMMIT;
